        constexpr uint32_t OUTLINK_EOF_ERR_INT_CLR = (1U << 6);  ///< Set this bit to clear UHCI_OUTLINK_EOF_ERR_INT interrupt.
        constexpr uint32_t APP_CTRL0_INT_CLR = (1U << 7);  ///< Set this bit to clear UHCI_APP_CTRL0_INT interrupt.
